command:
// Define the new token configuration
const newPrototypeTokenConfig = {
   appendNumber: true,
   displayBars: CONST.TOKEN_DISPLAY_MODES.HOVER
};

// Apply the new token configuration to all actors
game.actors.contents.forEach(actor => {
   if (actor.type == 'npc') {
      console.log(actor.name);
      actor.update({ "prototypeToken": newPrototypeTokenConfig });
   }
});

ui.notifications.info("Token configuration updated for all actors.");
