command:
//console.log(token);
//console.log(actor);

let currentPoints = actor.system.resources.heroPoints.value;
let newPoints = currentPoints - 1;
let message = "";

//ui.notifications.info(actor.name +" current points:" + currentPoints);
if(currentPoints > 0) {
   actor.update({ "system.resources.heroPoints.value": newPoints});
   message = actor.name +" now has " + newPoints + " Hero Points";
   ui.notifications.warn(message);
}
else {
   message = actor.name + " does not have enough Hero Points";
   ui.notifications.error(message);
}

ChatMessage.create({
    user: game.user._id,
    speaker: ChatMessage.getSpeaker(),
    content: message
});
